var verifyName = function(oForm) {
  oAux = getField('NAME');
        if (oAux.value == '') {
           new leimnud.module.app.alert().make({
          label:'@G::LoadTranslation(ID_PLEASE_ENTER_REQUIRED_FIELDS)'
    	    });
          oAux.focus();
          return;
        }
   else{  
        oForm.submit();
       }
};

function cancel(){
    window.location = 'skinsList';
}
