dynaForm.JS:
hideRowById('INPUT_DOCUMENT');

leimnud.event.add(getField('UPLOAD_OPTION'), 'change', function() {
  switch (this.value) {
    case '0':
                hideRowById('INPUT_DOCUMENT');               
    break;
    case '1':
                showRowById('INPUT_DOCUMENT');
    break;
  }
}.extend(getField('UPLOAD_OPTION')));
